mapper.select:
select boardno, userid, subject, content, hit, regtime
  from board 
 where boardno = #{no}

mapper.insert:
insert into board(
	userid, subject, content
) values (
	#{userId}, #{subject}, #{content}
)

mapper.update:
update board 
   set subject = #{subject}, 
   	   userid = #{userId}, 
   	   content = #{content}
 where boardno = #{boardno}
