mapper.select:
select username, userid, email
from user
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from user
where userid = #{userid}

mapper.update:
update user
userpwd = #{userpwd}, email = #{email},
where userid = #{userid}

mapper.select:
select count(userid)
from user
where userid = #{userid}

select userid, userpwd, username, email, date_format(joindate, '%y.%m.%d') joindate
from user
order by joindate desc;

select userid, userpwd, username, email, date_format(joindate, '%y.%m.%d') joindate
from user
where userid = #{userid}
