mapper.select:
select user_id as userId, user_nm as name, user_pwd as password, user_addr as address, user_contact as phone
from user
where user_id = #{userId} and user_pwd = #{password}

mapper.update:
update user
set user_pwd= #{password}, user_nm=#{name}, user_addr=#{address}, user_contact=#{phone} 
where user_id=#{userId}

mapper.select:
select user_pwd
from user
where user_id = #{userId} and user_nm = #{name}
